import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import SearchBar from '../SearchBar/SearchBar';
import { Box, HamburgerIcon, Menu, Text, Pressable } from 'native-base';

export default function Home({ navigation }) {
    const handleSearch = (searchText) => {
        // Implemente a l√≥gica de busca aqui com base no searchText
    };

    const [isMenuOpen, setIsMenuOpen] = useState(false);

    return (
        <Box flex={1} flexDirection="column">
            <Box
                backgroundColor="#fff"
                flexDirection="row"
                alignItems="center"
                justifyContent="space-between"
                padding={2}
            >
                <Pressable onPress={() => setIsMenuOpen(!isMenuOpen)}>
                    <HamburgerIcon size="6" />
                </Pressable>
                <Text>Seu Aplicativo</Text>
                <View />
            </Box>
            <Box flex={1} flexDirection="column">
                {isMenuOpen && (
                    <Menu
                        isOpen={isMenuOpen}
                        onClose={() => setIsMenuOpen(false)}
                        placement="left"
                        zIndex={1}
                    >
                        <Menu.Item
                            onPress={() => {
                                navigation.navigate('MediaCalculator');
                                setIsMenuOpen(false);
                            }}
                        >
                            Sistema de Notas
                        </Menu.Item>
                        <Menu.Item
                            onPress={() => {
                                navigation.navigate('Counter');
                                setIsMenuOpen(false);
                            }}
                        >
                            Sistema Contador
                        </Menu.Item>
                        <Menu.Item
                            onPress={() => {
                                navigation.navigate('ColorList');
                                setIsMenuOpen(false);
                            }}
                        >
                            Color List
                        </Menu.Item>
                        <Menu.Item
                            onPress={() => {
                                navigation.navigate('Galeria');
                                setIsMenuOpen(false);
                            }}
                        >
                            Galeria
                        </Menu.Item>
                    </Menu>
                )}
                <View style={styles.container}>
                    <View style={styles.searchBarContainer}>
                        <SearchBar onSearch={handleSearch} />
                    </View>
                </View>
            </Box>
        </Box>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    searchBarContainer: {
        padding: 20,
    },
});
