import React from 'react';
import { View, FlatList, Image, StyleSheet } from 'react-native';

const imagens = [
  { id: '1', uri: 'URL_DA_IMAGEM_1', height: 200, width: 150 },
  { id: '2', uri: 'URL_DA_IMAGEM_2', height: 250, width: 180 },
  { id: '3', uri: 'URL_DA_IMAGEM_3', height: 180, width: 220 },
  // Adicione mais imagens com diferentes alturas e larguras aqui
];

function Galeria() {
  const renderItem = ({ item }) => (
    <View style={[styles.item, { height: item.height, width: item.width }]}>
      <Image source={{ uri: item.uri }} style={styles.image} />
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={imagens}
        renderItem={renderItem}
        keyExtractor={item => item.id}
        horizontal={false}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  item: {
    margin: 10,
    borderWidth: 1,
    borderColor: '#ddd',
  },
  image: {
    flex: 1,
    width: null,
    height: null,
    resizeMode: 'cover',
  },
});

export default Galeria;
