import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import { Button, Menu, Appbar } from 'react-native-paper';
import SearchBar from '../SearchBar/SearchBar';

export default function Home({ navigation }) {
    const [menuVisible, setMenuVisible] = useState(false);

    const handleSearch = (searchText) => {
        // Implemente a lógica de busca aqui com base no searchText
    };

    return (
        <View style={styles.container}>
            <Appbar.Header>
                <Appbar.Action
                    icon="menu"
                    onPress={() => setMenuVisible(true)}
                />
                <Appbar.Content title="Título da Página" />
            </Appbar.Header>
            <SearchBar onSearch={handleSearch} />
            <Menu
                visible={menuVisible}
                onDismiss={() => setMenuVisible(false)}
                anchor={
                    <Appbar.Action icon="menu" onPress={() => setMenuVisible(true)} />
                }
            >
                <Menu.Item
                    onPress={() => {
                        navigation.navigate('MediaCalculator');
                        setMenuVisible(false);
                    }}
                    title="Sistema de Notas"
                />
                <Menu.Item
                    onPress={() => {
                        navigation.navigate('Counter');
                        setMenuVisible(false);
                    }}
                    title="Sistema Contador"
                />
                <Menu.Item
                    onPress={() => {
                        navigation.navigate('ColorList');
                        setMenuVisible(false);
                    }}
                    title="Color List"
                />
                <Menu.Item
                    onPress={() => {
                        navigation.navigate('Galeria');
                        setMenuVisible(false);
                    }}
                    title="Galeria"
                />
            </Menu>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    searchBarContainer: {
        padding: 20,
    },
    buttonContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    buttonSpace: {
        marginVertical: 10,
    },
});
