import React from 'react';
import { StyleSheet } from 'react-native';
import { Box, HamburgerIcon, Menu, NativeBaseProvider, Pressable } from 'native-base';

function Home({ navigation }) {
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    return (
        <div>
            <button onClick={toggleMenu}>
                ☰ {/* Ícone de hambúrguer */}
            </button>
            {menuOpen && (
                <div className="menu">
                    <ul>
                        <li onClick={() => navigation.navigate('MediaCalculator')}>
                            Sistema de Notas
                        </li>
                        <li onClick={() => navigation.navigate('Counter')}>
                            Sistema Contador
                        </li>
                        <li onClick={() => navigation.navigate('ColorList')}>
                            Color List
                        </li>
                        <li onClick={() => navigation.navigate('Galeria')}>Galeria</li>
                    </ul>
                </div>
            )}
        </div>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    searchBarContainer: {
        padding: 20,
    },
    buttonContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    buttonSpace: {
        marginVertical: 10,
    },
});

export default Home;
