import React from 'react';
import { StyleSheet, View } from 'react-native';
import SearchBar from '../SearchBar/SearchBar';
import { Drawer, Box, HamburgerIcon } from 'native-base';

export default function Home({ navigation }) {
    const handleSearch = (searchText) => {
        // Implemente a lógica de busca aqui com base no searchText
    };

    return (
        <Drawer
            placement="left"
            isOpen={false} // Defina como true para abrir o menu por padrão
            onClose={() => { }} // Lida com o fechamento do menu
            content={
                <Box w="90%" alignItems="center">
                    <View style={{ paddingVertical: 20 }}>
                        <HamburgerIcon />
                    </View>
                    <Box>
                        <Button
                            onPress={() => {
                                navigation.navigate('MediaCalculator');
                            }}
                        >
                            Sistema de Notas
                        </Button>
                        <Button
                            onPress={() => {
                                navigation.navigate('Counter');
                            }}
                        >
                            Sistema Contador
                        </Button>
                        <Button
                            onPress={() => {
                                navigation.navigate('ColorList');
                            }}
                        >
                            Color List
                        </Button>
                        <Button
                            onPress={() => {
                                navigation.navigate('Galeria');
                            }}
                        >
                            Galeria
                        </Button>
                    </Box>
                </Box>
            }
        >
            <View style={styles.container}>
                <View style={styles.searchBarContainer}>
                    <SearchBar onSearch={handleSearch} />
                </View>
            </View>
        </Drawer>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    searchBarContainer: {
        padding: 20,
    },
    buttonContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    buttonSpace: {
        marginVertical: 10,
    },
});
