import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import SearchBar from '../SearchBar/SearchBar';
import { Drawer, Box, HamburgerIcon, Menu } from 'native-base';

export default function Home({ navigation }) {
    const handleSearch = (searchText) => {
        // Implemente a l√≥gica de busca aqui com base no searchText
    };

    const [isOpen, setIsOpen] = useState(false);

    return (
        <Drawer
            placement="left"
            isOpen={isOpen}
            onClose={() => setIsOpen(false)}
            content={
                <Box w="90%" alignItems="center">
                    <View style={{ paddingVertical: 20 }}>
                        <HamburgerIcon onPress={() => setIsOpen(!isOpen)} />
                    </View>
                    <Box>
                        <Menu.Item
                            onPress={() => {
                                navigation.navigate('MediaCalculator');
                                setIsOpen(false);
                            }}
                        >
                            Sistema de Notas
                        </Menu.Item>
                        <Menu.Item
                            onPress={() => {
                                navigation.navigate('Counter');
                                setIsOpen(false);
                            }}
                        >
                            Sistema Contador
                        </Menu.Item>
                        <Menu.Item
                            onPress={() => {
                                navigation.navigate('ColorList');
                                setIsOpen(false);
                            }}
                        >
                            Color List
                        </Menu.Item>
                        <Menu.Item
                            onPress={() => {
                                navigation.navigate('Galeria');
                                setIsOpen(false);
                            }}
                        >
                            Galeria
                        </Menu.Item>
                    </Box>
                </Box>
            }
        >
            <View style={styles.container}>
                <View style={styles.searchBarContainer}>
                    <SearchBar onSearch={handleSearch} />
                </View>
            </View>
        </Drawer>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    searchBarContainer: {
        padding: 20,
    },
});
