import React from 'react';
import { View, FlatList, Image, StyleSheet } from 'react-native';
import cat1 from './Assets/cat1.jpg';
import cat2 from './Assets/cat2.jpg';
import cat3 from './Assets/cat3.jpg';
import cat4 from './Assets/cat4.jpg';
import cat5 from './Assets/cat5.png';
import cat6 from './Assets/cat6.jpg';
import cat7 from './Assets/cat7.jpg';
import cat8 from './Assets/cat8.jfif';
import cat9 from './Assets/cat9.jpg';
import cat10 from './Assets/cat10.jpg';
import cat11 from './Assets/cat11.jpg';
import cat12 from './Assets/cat12.jpg';
import cat13 from './Assets/cat13.jpg';
import cat14 from './Assets/cat14.jfif';


const imagens = [
  { id: '1', url: cat1, height: 200, width: 150 },
{ id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },
  { id: '1', url: cat1, height: 200, width: 150 },

];

function Galeria() {
  const renderItem = ({ item }) => (
    <View style={[styles.item, { height: item.height, width: item.width }]}>
      <Image source={{ uri: item.url }} style={styles.image} />
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={imagens}
        renderItem={renderItem}
        keyExtractor={item => item.id}
        horizontal={true}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  item: {
    margin: 10,
    borderWidth: 1,
    borderColor: '#ddd',
  },
  image: {
    flex: 1,
    width: null,
    height: null,
    resizeMode: 'cover',
  },
});

export default Galeria;
