import React from 'react';
import { StyleSheet, View, Button } from 'react-native';
import SearchBar from '../SearchBar/SearchBar'; // Importe o componente SearchBar

export default function Home({ navigation }) {
    const handleSearch = (searchText) => {
        // Implemente a lógica de busca aqui com base no searchText
        // Exemplo: navigation.navigate('SearchResults', { searchText });
    };

    return (
        <View style={styles.container}>
            <View style={styles.searchBarContainer}>
                <SearchBar onSearch={handleSearch} />
            </View>
            <View style={styles.buttonContainer}>
                <Button
                    title="Sistema de Notas"
                    onPress={() => navigation.navigate('MediaCalculator')}
                />
                <Button
                    title="Sistema Contador"
                    onPress={() => navigation.navigate('Counter')}
                />
                <Button
                    title="Color List"
                    onPress={() => navigation.navigate('ColorList')}
                />
                <Button title="Galeria" onPress={() => navigation.navigate('Galeria')} />
            </View>
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    searchBarContainer: {
        padding: 20, // Espaço de preenchimento ao redor da barra de pesquisa
    },
    buttonContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        marginVertical: 20
    },
    buttonSpace: {
        marginVertical: 10,
    },
});
