import React from 'react';
import { StyleSheet } from 'react-native';
import { Box, HamburgerIcon, Menu, NativeBaseProvider, Pressable } from 'native-base';

function Home({ navigation }) {
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    return (
        <Box w="100%" p={4}>
            <Menu
                autoAssignAccessibilityRole={true}
                placement="top"
                trigger={(triggerProps) => {
                    return (
                        <Pressable {...triggerProps}>
                            <HamburgerIcon />
                        </Pressable>
                    );
                }}
            >
                <Menu.Item
                    onPress={() => navigation.navigate('MediaCalculator')}
                    children="Sistema de Notas"
                />
                <Menu.Item
                    onPress={() => navigation.navigate('Counter')}
                    children="Sistema Contador"
                />
                <Menu.Item
                    onPress={() => navigation.navigate('ColorList')}
                    children="Color List"
                />
                <Menu.Item
                    onPress={() => navigation.navigate('Galeria')}
                    children="Galeria"
                />
            </Menu>
        </Box>
    );
}

export default () => {
    return (
        <NativeBaseProvider>
            <Home /> {/* Certifique-se de que este componente está dentro de um contexto de navegação */}
        </NativeBaseProvider>
    );
};
