import React from 'react';
import { StyleSheet } from 'react-native';
import { Box, Center, HamburgerIcon, Menu, NativeBaseProvider, Pressable } from 'native-base';

function Home({ navigation }) {
    const handleSearch = (searchText) => {
        // Implemente a l√≥gica de busca aqui com base no searchText
    };

    return (
        <Box w="100%" p={4}>
            <Menu
                autoAssignAccessibilityRole={true}
                placement="top" // Altere o placement para "top"
                trigger={(triggerProps) => {
                    return (
                        <Pressable {...triggerProps}>
                            <HamburgerIcon />
                        </Pressable>
                    );
                }}
            >
                <Menu.Item
                    onPress={() => navigation.navigate('MediaCalculator')}
                    children="Sistema de Notas"
                />
                <Menu.Item
                    onPress={() => navigation.navigate('Counter')}
                    children="Sistema Contador"
                />
                <Menu.Item
                    onPress={() => navigation.navigate('ColorList')}
                    children="Color List"
                />
                <Menu.Item
                    onPress={() => navigation.navigate('Galeria')}
                    children="Galeria"
                />
            </Menu>
        </Box>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    searchBarContainer: {
        padding: 20,
    },
    buttonContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    buttonSpace: {
        marginVertical: 10,
    },
});

export default () => {
    return (
        <NativeBaseProvider>
            <Home />
        </NativeBaseProvider>
    );
};
