import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import { Button, Menu, Divider, Provider, IconButton } from 'react-native-paper';
import SearchBar from '../SearchBar/SearchBar';

export default function Home({ navigation }) {
    const [menuVisible, setMenuVisible] = useState(false);

    const handleSearch = (searchText) => {
        // Implemente a l√≥gica de busca aqui com base no searchText
    };

    return (
        <Provider>
            <View style={styles.container}>
                <View style={styles.header}>
                    <SearchBar onSearch={handleSearch} />
                    <IconButton
                        icon="menu"
                        onPress={() => setMenuVisible(!menuVisible)}
                    />
                </View>
                <Menu
                    visible={menuVisible}
                    onDismiss={() => setMenuVisible(false)}
                    anchor={
                        <Button
                            icon="menu"
                            onPress={() => setMenuVisible(true)}
                            style={{ position: 'absolute', top: 0, left: 0 }}
                        >
                            Menu
                        </Button>
                    }
                >
                    <Menu.Item
                        onPress={() => {
                            navigation.navigate('MediaCalculator');
                            setMenuVisible(false);
                        }}
                        title="Sistema de Notas"
                    />
                    <Divider />
                    <Menu.Item
                        onPress={() => {
                            navigation.navigate('Counter');
                            setMenuVisible(false);
                        }}
                        title="Sistema Contador"
                    />
                    <Divider />
                    <Menu.Item
                        onPress={() => {
                            navigation.navigate('ColorList');
                            setMenuVisible(false);
                        }}
                        title="Color List"
                    />
                    <Divider />
                    <Menu.Item
                        onPress={() => {
                            navigation.navigate('Galeria');
                            setMenuVisible(false);
                        }}
                        title="Galeria"
                    />
                </Menu>
            </View>
        </Provider>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    header: {
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: 20,
    },
    buttonContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    buttonSpace: {
        marginVertical: 10,
    },
});
