import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import SearchBar from '../SearchBar/SearchBar';
import { Box, HamburgerIcon, Menu, Text, Pressable } from 'native-base';

export default function Home({ navigation }) {
    const handleSearch = (searchText) => {
        // Implemente a l√≥gica de busca aqui com base no searchText
    };

    <Box w="90%" alignItems="left">
        <Menu w="190" trigger={triggerProps => {
            return <Pressable accessibilityLabel="More options menu" {...triggerProps}>
                <HamburgerIcon />
            </Pressable>;
        }}>
            <Menu.Item
                title="Sistema de Notas"
                onPress={() => navigation.navigate('MediaCalculator')} />
            <Menu.Item
                title="Sistema Contador"
                onPress={() => navigation.navigate('Counter')} />
            <Menu.Item
                title="Color List"
                onPress={() => navigation.navigate('ColorList')} />
            <Menu.Item
                title="Galeria"
                onPress={() => navigation.navigate('Galeria')} />
        </Menu>
    </Box>;
    <View style={styles.container}>
        <View style={styles.searchBarContainer}>
            <SearchBar onSearch={handleSearch} />
        </View>
    </View>

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    searchBarContainer: {
        padding: 20,
    },
    buttonContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
    buttonSpace: {
        marginVertical: 10,
    },
});
