import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import { Button, Menu, Divider, Provider } from 'react-native-paper';
import SearchBar from '../SearchBar/SearchBar';

export default function Home({ navigation }) {
    const [visible, setVisible] = useState(false);
    const [anchor, setAnchor] = useState(null);

    const openMenu = (event) => {
        setAnchor(event.currentTarget);
        setVisible(true);
    };

    const closeMenu = () => setVisible(false);

    const handleNavigation = (screenName) => {
        navigation.navigate(screenName);
        closeMenu();
    };

    const handleSearch = (searchText) => {
        // Implemente a l√≥gica de busca aqui com base no searchText
    };

    return (
        <Provider>
            <View style={styles.container}>
                <View style={styles.searchBarContainer}>
                    <SearchBar onSearch={handleSearch} />
                </View>
                <View style={styles.buttonContainer}>
                    <Button
                        onPress={(e) => openMenu(e)}
                        icon="menu"
                        mode="outlined"
                        color="#007aff"
                    >
                        Menu
                    </Button>
                    <Menu
                        visible={visible}
                        onDismiss={closeMenu}
                        anchor={anchor}
                        contentStyle={styles.menu}
                    >
                        <Menu.Item
                            onPress={() => handleNavigation('MediaCalculator')}
                            title="Sistema de Notas"
                        />
                        <Divider />
                        <Menu.Item
                            onPress={() => handleNavigation('Counter')}
                            title="Sistema Contador"
                        />
                        <Divider />
                        <Menu.Item
                            onPress={() => handleNavigation('ColorList')}
                            title="Color List"
                        />
                        <Divider />
                        <Menu.Item
                            onPress={() => handleNavigation('Galeria')}
                            title="Galeria"
                        />
                    </Menu>
                </View>
            </View>
        </Provider>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    searchBarContainer: {
        padding: 20,
    },
    buttonContainer: {
        padding: 20,
    },
    menu: {
        marginTop: 40,
    },
});
