import React, { useState } from 'react';
import { StyleSheet, View } from 'react-native';
import { Button, Menu } from 'react-native-paper';
import SearchBar from '../SearchBar/SearchBar';

export default function Home({ navigation }) {
    const [visible, setVisible] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);

    const handleSearch = (searchText) => {
        // Implemente a lógica de busca aqui com base no searchText
    };

    const openMenu = () => setVisible(true);
    const closeMenu = () => setVisible(false);

    const handleMenuItemPress = (item) => {
        setSelectedItem(item);
        closeMenu();
        // Navegue para o componente correto com base no item selecionado
        switch (item) {
            case 'Sistema de Notas':
                navigation.navigate('MediaCalculator');
                break;
            case 'Sistema Contador':
                navigation.navigate('Counter');
                break;
            case 'Color List':
                navigation.navigate('ColorList');
                break;
            case 'Galeria':
                navigation.navigate('Galeria');
                break;
            default:
                break;
        }
    };

    const menuItems = [
        'Sistema de Notas',
        'Sistema Contador',
        'Color List',
        'Galeria',
    ];

    return (
        <View style={styles.container}>
            <View style={styles.searchBarContainer}>
                <SearchBar onSearch={handleSearch} />
            </View>
            <View style={styles.buttonContainer}>
                <Button onPress={openMenu}>Opções</Button>
                <Menu
                    visible={visible}
                    onDismiss={closeMenu}
                    anchor={<Button onPress={openMenu}>Opções</Button>}
                >
                    {menuItems.map((item) => (
                        <Menu.Item
                            key={item}
                            onPress={() => handleMenuItemPress(item)}
                            title={item}
                        />
                    ))}
                </Menu>
            </View>
            {selectedItem && (
                <View style={styles.selectedItemContainer}>
                    {/* Renderize o conteúdo do componente selecionado aqui */}
                    {/* Por exemplo: */}
                    {/* <Text>{selectedItem}</Text> */}
                </View>
            )}
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    searchBarContainer: {
        padding: 20,
    },
    buttonContainer: {
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: 10,
    },
    selectedItemContainer: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
    },
});
