import React from 'react';
import { View, FlatList, Image, StyleSheet, Dimensions } from 'react-native';
import cat1 from './Assets/cat1.jpg';
import cat2 from './Assets/cat2.jpg';
import cat3 from './Assets/cat3.jpg';
import cat4 from './Assets/cat4.jpg';
import cat5 from './Assets/cat5.png';
import cat6 from './Assets/cat6.jpg';
import cat7 from './Assets/cat7.jpg';
import cat8 from './Assets/cat8.jfif';
import cat9 from './Assets/cat9.jpg';
import cat10 from './Assets/cat10.jpg';
import cat11 from './Assets/cat11.jpg';
import cat12 from './Assets/cat12.jpg';
import cat13 from './Assets/cat13.jpg';
import cat14 from './Assets/cat14.jfif';

const imagens = [
  { id: '1', url: cat1, height: 200, width: 200 },
  { id: '2', url: cat2, height: 250, width: 250 },
  { id: '3', url: cat3, height: 300, width: 300 },
  { id: '4', url: cat4, height: 350, width: 350 },
  { id: '5', url: cat5, height: 400, width: 400 },
  { id: '6', url: cat6, height: 450, width: 450 },
  { id: '7', url: cat7, height: 500, width: 500 },
  { id: '8', url: cat8, height: 550, width: 550 },
  { id: '9', url: cat9, height: 600, width: 600 },
  { id: '10', url: cat10, height: 650, width: 650 },
  { id: '11', url: cat11, height: 700, width: 700 },
  { id: '12', url: cat12, height: 50, width: 50 },
  { id: '13', url: cat13, height: 100, width: 100 },
  { id: '14', url: cat14, height: 150, width: 150 },
];

function Galeria() {
  const renderItem = ({ item }) => (
    <View style={[styles.item, { height: item.height, width: item.width }]}>
      <Image source={{ uri: item.url }} style={styles.image} />
    </View>
  );

  return (
    <View style={styles.container}>
      <FlatList
        data={imagens}
        renderItem={renderItem}
        keyExtractor={item => item.id}
        numColumns={2}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  item: {
    margin: 10,
    borderWidth: 1,
    borderColor: '#ddd',
     justifyContent: 'center', // Centraliza verticalmente
    alignItems: 'center',
  },
  image: {
    flex: 1,
    width: null,
    height: null,
    resizeMode: 'contain', // 'contain' para manter a proporção e mostrar a imagem completa
  },
});

export default Galeria;
